import { DatetimePicker } from 'mint-ui';
import moment from 'moment';
import Vue from "vue";
Vue.component(DatetimePicker.name, DatetimePicker);
export default {
  name: 'AndroidCompileInform',
  data: function data() {
    return {
      getnEschoolUrl: this.$store.state.eschool,
      sInformTle: "",
      sInformPop: false,
      sInformPopText: "",
      sStatusPop: false,
      sStatusPopText: "",
      sUrgency: false,
      sUrgencyText: "",
      imgDataUrl: [],
      imgDataFile: [],
      aImg: [],
      imgDataFileIndex: 0,
      dateTime: '',
      startDate: new Date(),
      sPublishText: '',
      sPublishId: "",
      sReceptionText: '',
      sAbstractText: '',
      sContentText: '',
      sSave: false,
      sDelete: false,
      announcementId: "",
      yearText: "",
      monthText: "",
      dayText: ""
    };
  },
  watch: {
    yearText: function yearText(val, oldVal) {
      //普通的watch监听
      if (val.length > 4) {
        this.yearText = val.substring(0, 4);
      }
    },
    monthText: function monthText(val, oldVal) {
      //普通的watch监听
      if (val.length > 2) {
        this.monthText = val.substring(0, 2);
      } else {
        var num = parseInt(val);

        if (num < 1) {
          this.monthText = "1";
        } else if (num > 12) {
          this.monthText = "12";
        }
      }
    },
    dayText: function dayText(val, oldVal) {
      //普通的watch监听
      if (val.length > 2) {
        this.dayText = val.substring(0, 2);
      } else {
        var num = parseInt(val);

        if (num < 1) {
          this.dayText = "1";
        } else if (num > 31) {
          this.dayText = "31";
        }
      }
    }
  },
  components: {// 组件实例化
  },
  mounted: function mounted() {},
  created: function created() {
    document.title = "编辑通知公告";
    document.body.style.background = "#F8F8F8";
    var that = this;

    if (that.$route.params.id) {
      that.$http({
        method: "GET",
        url: that.getnEschoolUrl + "api/announcement/v0.1/announcements/" + that.$route.params.id + "/detail ",
        headers: {
          accessToken: sessionStorage.accessToken,
          schoolId: sessionStorage.schoolId
        }
      }).then(function (response) {
        console.log(response);
        that.sInformTle = response.data.title;

        if (response.data.type == 1) {
          that.sInformPopText = "通知";
        } else if (response.data.type == 2) {
          that.sInformPopText = "公告";
        }

        if (response.data.status == 1) {
          that.sStatusPopText = "启用";
        } else if (response.data.status == 2) {
          that.sStatusPopText = "停用";
        }

        if (response.data.urgency == 1) {
          that.sUrgencyText = "是";
        } else if (response.data.urgency == 2) {
          that.sUrgencyText = "否";
        }

        var aaa = response.data.urgencyDeadline.split(" ");
        var bbb = aaa[0].split("-");
        that.yearText = bbb[0];
        that.monthText = bbb[1];
        that.dayText = bbb[2];
        that.dateTime = response.data.urgencyDeadline;
        that.sPublishText = response.data.name;
        that.sPublishId = response.data.groupId;
        that.sReceptionText = [];

        for (var index = 0; index < response.data.receiveGroups.length; index++) {
          var element = response.data.receiveGroups[index];
          that.sReceptionText.push(element.groupId);
        }

        that.sAbstractText = response.data.digest;
        that.sContentText = response.data.content;
        that.announcementId = response.data.id;
      }).catch(function (error) {
        console.log(error);
      });
    } else {
      that.sInformTle = sessionStorage.sInformTle || "";
      that.sInformPopText = sessionStorage.sInformPopText || "";
      that.sStatusPopText = sessionStorage.sStatusPopText || "";
      that.sUrgencyText = sessionStorage.sUrgencyText || "";
      that.yearText = sessionStorage.sYearText || "";
      that.monthText = sessionStorage.sMonthText || "";
      that.dayText = sessionStorage.sDayText || "";
      that.sPublishText = sessionStorage.sPublishText || "";
      that.sPublishId = sessionStorage.sPublishId || "";
      that.sReceptionText = sessionStorage.sReceptionText || "";
      that.sAbstractText = sessionStorage.sAbstractText || "";
      that.sContentText = sessionStorage.sContentText || "";
      that.imgDataUrl = sessionStorage.imgDataUrl || [];
      that.imgDataFile = sessionStorage.imgDataFile || [];
      that.announcementId = sessionStorage.announcementId || '';
    }

    that.common.setingTtitle('编辑通知公告');
  },
  methods: {
    //删除通知/公告
    deleteInform: function deleteInform() {
      var that = this;
      that.$http({
        method: "DELETE",
        url: that.getnEschoolUrl + "api/announcement/v0.1/announcements/" + that.$route.params.id,
        headers: {
          accessToken: sessionStorage.accessToken,
          schoolId: sessionStorage.schoolId
        }
      }).then(function (response) {
        console.log(response);

        if (response.data.result) {
          alert('删除成功');
          that.$router.push({
            name: "AndroidInformList"
          }); // that.$alert('删除成功', {
          //     confirmButtonText: '确定',
          //     callback: action => {
          //         that.$router.push({
          //             name: "InformList",
          //         });
          //     }
          // });
        } else {
          alert('删除失败，请刷新重试'); // that.$alert('删除失败，请刷新重试', {
          //     confirmButtonText: '确定',
          // });
        }
      }).catch(function (error) {
        console.log(error);
      });
    },
    //发布类型弹窗
    informPop: function informPop() {
      var that = this;
      that.sInformPop = !that.sInformPop;
      that.sInformPop ? that.sPopBg = true : that.sPopBg = false;
    },
    //状态弹窗
    statusPop: function statusPop() {
      var that = this;
      that.sStatusPop = !that.sStatusPop;
      that.sStatusPop ? that.sPopBg = true : that.sPopBg = false;
    },
    //是否紧急弹窗
    urgencyPop: function urgencyPop() {
      var that = this;
      that.sUrgency = !that.sUrgency;
      that.sUrgency ? that.sPopBg = true : that.sPopBg = false;
    },
    //阻止冒泡
    popContent: function popContent() {
      return;
    },
    //发布类型文字赋值
    informPopText: function informPopText(text) {
      var that = this;
      that.sInformPopText = text;
      that.sInformPop = !that.sInformPop;
      that.sInformPop ? that.sPopBg = true : that.sPopBg = false;
    },
    //状态文字赋值
    statusPopText: function statusPopText(text) {
      var that = this;
      that.sStatusPopText = text;
      that.sStatusPop = !that.sStatusPop;
      that.sStatusPop ? that.sPopBg = true : that.sPopBg = false;
    },
    //是否紧急赋值
    urgencyText: function urgencyText(text) {
      var that = this;
      that.sUrgencyText = text;
      that.sUrgency = !that.sUrgency;
      that.sUrgency ? that.sPopBg = true : that.sPopBg = false;
    },
    //上传图片
    addImgs: function addImgs(event) {
      var files = document.getElementById("upload").files;
      var that = this;
      var num = that.imgDataUrl.length + files.length;
      var image = new FormData();

      if (num <= 3) {
        for (var index = 0; index < files.length; index++) {
          that.getObjectURL(files[index]);
        }
      } else {
        alert("最多可上传3张图片");
      }
    },
    //图片转换格式
    getObjectURL: function getObjectURL(file) {
      var that = this;
      var reader = new FileReader();
      reader.readAsDataURL(file);

      reader.onloadend = function (e) {
        that.imgDataUrl.push(this.result);
        that.imgDataFile.push(file);
      };
    },
    //删除图片
    removeImgs: function removeImgs(index) {
      var that = this;
      that.imgDataUrl.splice(index, 1);
      that.imgDataFile.splice(index, 1); // console.log(index)
    },
    //开启时间选择器
    openPicker: function openPicker() {
      this.$refs.picker.open();
    },
    //点击确定按钮
    handleConfirm: function handleConfirm(data) {
      var date = moment(data).format('YYYY.MM.DD');
      this.dateTime = date;
      var that = this;
      Vue.nextTick(function () {
        that.$refs.picker.close();
      });
    },
    //点击取消按钮
    closeConfirm: function closeConfirm() {
      var that = this;
      Vue.nextTick(function () {
        that.$refs.picker.close();
      });
    },
    //发布/接收阻止跳转页面
    skipPages: function skipPages(pages) {
      var that = this;
      sessionStorage.setItem("sInformTle", that.sInformTle);
      sessionStorage.setItem("sInformPopText", that.sInformPopText);
      sessionStorage.setItem("sStatusPopText", that.sStatusPopText);
      sessionStorage.setItem("sUrgencyText", that.sUrgencyText);
      sessionStorage.setItem("dateTime", that.dateTime);
      sessionStorage.setItem("sYearText", that.yearText);
      sessionStorage.setItem("sMonthText", that.monthText);
      sessionStorage.setItem("sDayText", that.dayText);
      sessionStorage.setItem("sPublishText", that.sPublishText);
      sessionStorage.setItem("sPublishId", that.sPublishId);
      sessionStorage.setItem("sReceptionText", that.sReceptionText);
      sessionStorage.setItem("sAbstractText", that.sAbstractText);
      sessionStorage.setItem("sContentText", that.sContentText);
      sessionStorage.setItem("announcementId", that.announcementId);
      sessionStorage.setItem("imgDataUrl", that.imgDataUrl);
      sessionStorage.setItem("imgDataFile", that.imgDataFile);
      that.$router.push({
        name: pages,
        params: {
          "pagesUrl": "AndroidCompileInform"
        }
      });
    },
    //确认提交按钮，查看有没有上传图片
    cpmpileInform: function cpmpileInform() {
      var that = this;

      var md5 = require('md5-node');

      if (that.imgDataUrl.length == 0) {
        that.submitData();
      } else {
        var element = that.imgDataFile[that.imgDataFileIndex];
        var key = element.name.split('.'),
            type = key[key.length - 1];
        key[key.length - 1] = '';
        key = md5(key.join('.') + new Date().getTime()) + '.' + type;
        that.$http({
          method: "GET",
          url: that.getnEschoolUrl + "api/cms/qiniu/v0.1/open/token",
          headers: {
            'Content-Type': 'multipart/form-data',
            //之前说的以表单传数据的格式来传递fromdata
            accessToken: sessionStorage.accessToken,
            schoolId: sessionStorage.schoolId
          },
          params: {
            key: key
          }
        }).then(function (response) {
          var formData = new FormData();
          formData.append('file', element);
          formData.append('token', response.data.uptoken);
          formData.append('key', key);
          that.getQiuNiuUrl(formData);
        }).catch(function (error) {
          console.log(error);
        });
      }
    },
    //提交数据
    submitData: function submitData(img) {
      var that = this;

      var qs = require('qs');

      var title, content, digest, type, status, groupId, urgency, urgencyDeadline, receiveGroups;

      if (img == undefined) {
        img = '';
      } else {
        img = img.join(',');
      }

      title = that.sInformTle;
      content = that.sContentText;
      digest = that.sAbstractText;
      groupId = that.sPublishId;

      if (typeof that.sReceptionText == "string") {
        receiveGroups = that.sReceptionText.split(',');
      } else {
        receiveGroups = that.sReceptionText;
      }

      if (that.sInformPopText == "通知") {
        type = 1;
      } else if (that.sInformPopText == "公告") {
        type = 2;
      }

      if (that.sStatusPopText == "启用") {
        status = 1;
      } else if (that.sStatusPopText == "停用") {
        status = 2;
      }

      if (that.sUrgencyText == "是") {
        urgency = 1;
      } else if (that.sUrgencyText == "否") {
        urgency = 2;
      }

      urgencyDeadline = that.yearText + "-" + that.monthText + "-" + that.dayText;
      var obj = {
        "announcementId": that.announcementId,
        "title": title,
        "content": content,
        "digest": digest,
        "type": type,
        "status": status,
        "groupId": groupId,
        "urgency": urgency,
        "urgencyDeadline": urgencyDeadline + " 23:59",
        "receiveGroups": receiveGroups,
        "img": img
      };
      obj.urgencyDeadline = obj.urgencyDeadline.split(' ');
      obj.urgencyDeadline = obj.urgencyDeadline[0] + " " + obj.urgencyDeadline[1];
      console.log(obj.urgencyDeadline);
      that.$http({
        method: "POST",
        url: that.getnEschoolUrl + "api/announcement/v0.1/updateAppAnnouncements/" + that.announcementId,
        headers: {
          accessToken: sessionStorage.accessToken,
          schoolId: sessionStorage.schoolId,
          "Content-Type": "application/json;charset=UTF-8"
        },
        data: obj
      }).then(function (response) {
        that.imgDataFileIndex = 0;
        alert('提交成功');
        that.$router.push({
          name: "AndroidInformList"
        }); // that.$alert('提交成功', {
        //     confirmButtonText: '确定',
        //     callback: action => {
        //         that.$router.push({
        //             name: "InformList",
        //         });
        //     }
        // });
      }).catch(function (error, a) {
        that.imgDataFileIndex = 0;
        alert('提交失败，请刷新重试'); // that.$alert('提交失败，请刷新重试', {
        //     confirmButtonText: '确定',
        // });
      });
    },
    compileSave: function compileSave() {
      var that = this;
      var d = new Date();

      if (that.yearText.length != 0 && that.yearText.length == 4 && that.monthText.length != 0 && that.dayText.length != 0) {
        var yearDate = d.getFullYear();
        var getMonth = d.getMonth() + 1;
        var getDate = d.getDate();
        var strDate = yearDate + "-" + getMonth + "-" + getDate;
        var dateText = that.yearText + "-" + that.monthText + "-" + that.dayText;
        var aaa = that.dateFn(dateText, strDate);

        if (aaa) {
          var returnText = window.confirm("确认保存？");

          if (returnText) {
            that.cpmpileInform();
          }
        } else {
          alert('填写日期应大于等于当前日期');
        }
      } else {
        alert('请填写正确的截止时间');
      }
    },
    compileDelete: function compileDelete() {
      var that = this;
      var returnText = window.confirm("确认删除？");

      if (returnText) {
        that.deleteInform();
      }
    },
    dateFn: function dateFn(date1, date2) {
      var oDate1 = new Date(date1);
      var oDate2 = new Date(date2);

      if (oDate1.getTime() >= oDate2.getTime()) {
        return true;
      } else {
        return false;
      }
    }
  }
};
