#AddInform{
    .tle{
        background: #fff;
        padding:  0 .32rem;
        height: .96rem;
        display: flex;
        align-items: center;
        margin-top: .2rem;
        span{
            font-size: .32rem;
            width: 2rem;
            color: #333333;
        }
        p{
            flex: 1;
            overflow: hidden;
            input{
                font-size: .32rem;
                width: 100%;
                line-height: .6rem;
            }
        }
    }
    .publicList{
        margin-top: .2rem;
        li{
            padding:  0 .32rem;
            height: .96rem;
            background: #fff url('../assets/images/icon_arrowsR3.png') right .32rem center/.4rem no-repeat;
            border-bottom: 1px solid #EEEEEE;
            display: flex;
            align-items: center;
            span{
                font-size: .32rem;
                width: 2rem;
                color: #333333;
            }
            p{
                flex: 1;
                font-size: .32rem;
                overflow: hidden;
                color: #333333;
                text-align: right;
                padding-right: .48rem;
                input{
                    font-size: .32rem;
                    width: 100%;
                    text-align: right;
                    background: none;
                    line-height: .6rem;
                }
                a{
                    color: #999999;
                }
                em{
                    font-style: normal;
                }
            }
            &:last-of-type{
                border: 0;
            }
        }
    }
    .upload{
        margin-top: .2rem;
        padding: 0 .32rem .32rem;
        background: #fff;
        p{
            font-size: .24rem;
            padding: .32rem 0 .12rem;
            color: #999999;
        }  
        .uploadContent{
            overflow: hidden;
            ul{
                float: left;
                display: flex;
                padding-top: .2rem;
                li{
                    width: 1.6rem;
                    border: 1px solid #e0e0e0;
                    height: 1.6rem;
                    margin-right: .5rem;
                    position: relative;
                    span{
                        overflow: hidden;
                        img{
                            height: 100%;
                        }
                    }
                    i{
                        position: absolute;
                        right: -.2rem;
                        top: -.2rem;
                        width: .4rem;
                        height: .4rem;
                    }
                    
                    
                }
            }
            div{
                float: left;
                width: 1.6rem;
                height: 1.6rem;
                margin-top: .2rem;
                border: 2px dashed #e0e0e0;
                box-sizing: border-box;
                line-height: 1.4rem;
                text-align: center;
                font-size: .72rem;
                color: #e0e0e0;
                position: relative;
                input{
                    position: absolute;
                    left: 0;
                    top: 0;
                    width: 100%;
                    height: 100%;
                    z-index: 10;
                    opacity: 0;
                }
            }
        }
        
    }
    .publicContent{
        margin-top: .2rem;
        padding: .3rem .32rem;
        background: #fff;
        display: flex;
        span{
            width: 2rem;
            font-size: .32rem;
            color: #333333;
        }
        p{
            flex: 1;
            textarea{
                width: 100%;
                height: 2rem;
                font-size: .32rem;
                border: 0;
            }
        }
    }
    .compileBtn{
        height: .96rem;
        display: flex;
        justify-content: center;
        align-items: center;
        background: #fff;
        margin-top: .2rem;
        border-top: 1px solid #e0e0e0;
        font-size: .32rem;
        span{
            flex: 1;
            text-align: center;
            color: #03A9F4;
            border-right: 1px solid #e0e0e0;
        }
        p{
            flex: 1;
            text-align: center;
            color: #999999;
        }
    }
    .popBg{
        position: fixed;
        width: 100%;
        height: 100%;
        background: rgba(0,0,0,0.40);
        top: 0;
        left: 0;
        .popContent{
            display: flex;
            align-items: flex-end;
            justify-content: center;
            position: absolute;
            width: 100%;
            height: 100%;
            &.bottom{
                align-items: flex-end;
            }
            &.center{
                align-items: center;
            }
            .cancel{
                width: 100%;
                div{
                    background: #fff;
                    margin: 0 .32rem;
                    display: flex;
                    flex-direction: column;
                    border-radius:12px; 
                    a{
                        line-height: .9rem;
                        font-size: .26rem;
                        color: #8F8E94;
                        text-align: center;
                    }
                    span{
                        line-height: 1.14rem;
                        font-size: .4rem;
                        color: #0076FF;
                        text-align: center;
                        border-top: 1px solid #EEEEEE;
                    }
                    b{
                        line-height: 1.08rem;
                        font-size: .4rem;
                        color: #0076FF;
                        text-align: center;
                        border-top: 1px solid #EEEEEE;
                        font-weight: normal;
                    }
                }
                p{
                    background: #fff;
                    margin: 0.2rem .32rem;
                    border-radius:12px; 
                    line-height: 1.14rem;
                    font-size: .4rem;
                    color: #0076FF;
                    text-align: center;
                    font-weight: bold;
                }
            }
            .selectPop{
                width: 5.4rem;
                background: #fff;
                border-radius: 13px;
                p{
                    font-size: .34rem;
                    font-weight: bold;
                    text-align: center;
                    padding: .38rem 0;  
                }
                div{
                    border-top: 1px solid #e0e0e0;
                    line-height: .88rem;
                    display: flex;
                    span{
                        flex:1;
                        text-align: center;
                        font-size: .34rem;
                        color: #007AFF;
                        border-right: 1px solid #e0e0e0;
                    }
                    a{
                        flex:1;
                        text-align: center;
                        font-size: .34rem;
                        color: #007AFF;
                    }
                }
            }
            .selectPop{
                width: 5.4rem;
                background: #fff;
                border-radius: 13px;
                p{
                    font-size: .34rem;
                    font-weight: bold;
                    text-align: center;
                    padding: .38rem 0;  
                }
                div{
                    border-top: 1px solid #e0e0e0;
                    line-height: .88rem;
                    display: flex;
                    span{
                        flex:1;
                        text-align: center;
                        font-size: .34rem;
                        color: #007AFF;
                        border-right: 1px solid #e0e0e0;
                    }
                    a{
                        flex:1;
                        text-align: center;
                        font-size: .34rem;
                        color: #007AFF;
                    }
                }
            }
            
        }
    }
}
